import React from "react";
import coursesPic3 from "../images/courses/pic3.jpg";

function CoursesComponent(props) {

  var array = []
  const getIds = (id) => {



    try{
      let storage = localStorage.getItem("courseId's")
      console.log("storage",storage)
      if(storage === null){
        localStorage.setItem("courseId's",array)
      }else if(storage !== null){
        if(array.includes(id)){

        }else{
          array.push(id)
          array.push(storage)
          console.log("selected ids====> ",array)
        }
      }

    } 
    catch(error){
      console.log("sadsadasdasdas",error)
    } 
  }

  console.log(props);
  if (props.loading) {
    return <h2>Loading...</h2>;
  }
  return (
    <>
      {props.courses !== undefined &&
        props.courses.map((item) => (
          <div className="col-md-6 col-lg-4 col-sm-6 m-b30" key={item.id}>
            <div
              className="cours-bx shadow cursor"
              // onClick={() => props.history.push("/courses-details", item.id)}
              onClick={()=>  { 
                // alert(item.id)
                getIds(item.id)

              }}
            >
              <div className="action-box">
                <img src={coursesPic3} alt={item.Title} />
                <span
                  onClick={() =>
                    // props.history.push("/courses-details", item.id)
                    alert(item.id)
                  }
                  className="btn"
                >
                  Read More 
                </span>
              </div>
              <div className="info-bx">
                <span>{"Islamic"}</span>
                <h6 style={{ textTransform: "uppercase" }}>{item.Title}</h6>
              </div>
            </div>
          </div>
        ))}
    </>
  );
}

export default CoursesComponent;
