import React,{Component} from 'react'; 
import { Link } from 'react-router-dom';
import Slider from "react-slick";

// Images
import coursesPic1 from '../../images/courses/pic10.jpg';



const PopularCoursesSlider2 = (props) => {

	   useEffect(() => {

		fetch('https://quran-server.herokuapp.com/course').then((res)=> res.json()).then((res) => {
			console.log("ssssss",res)
			this.setState({courses: res})
	})

	  })


		const courses = this.state.courses
		const settings = {
			infinite: true,
			dots: false,
			speed: 700,
			slidesToShow: 3,
			slidesToScroll: 3,

		};



		return(
			<>
				<Slider {...settings} className="courses-carousel-2 slick-slider owl-none">
					{courses!==undefined && courses.map((item)=>(
						<div className="slider-item">
							<div className="cours-bx">
								<div className="action-box">
									<img src={coursesPic1} alt={item.Title}/>
									<Link to="" onClick={() => this.props.history.push("/courses-details",item.id)} className="btn">Read More</Link>
								</div>
								<div className="info-bx">
									<h6 style={{ textTransform: 'uppercase' }}><Link to="" onClick={() => this.props.history.push("/courses-details",item.id)}>{item.Title}</Link></h6>
								</div>
								<div className="cours-more-info">
									{/* <div className="review">
										<span>3 Review</span>
										<ul className="cours-star">
											<li className="active"><i className="fa fa-star"></i></li>
											<li className="active"><i className="fa fa-star"></i></li>
											<li className="active"><i className="fa fa-star"></i></li>
											<li><i className="fa fa-star"></i></li>
											<li><i className="fa fa-star"></i></li>
										</ul>
									</div> */}
									{/* <div className="price">
										<del>$190</del>
										<h5>$120</h5>
									</div> */}
								</div>
							</div>
						</div>
					))}
				</Slider>
			</>
		);
	}


export default PopularCoursesSlider2;
