import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Form } from "react-bootstrap";
import { GoogleLogin } from "react-google-login";
import FacebookLogin from "react-facebook-login/dist/facebook-login-render-props";

import Alert from "react-bootstrap/Alert";

// Images
import logo from "../../images/QURAN-04.png";

import logoWhite2 from "../../images/logo-white-2.png";
import bannerImg from "../../images/background/bg2.jpg";

const Register = (props) => {
  const [variant, setVariant] = useState("success");
  const [open, setOpen] = useState(false);
  const [alertIcon, setAlertIcon] = useState();
  const [message, setMessage] = useState("this is example alert");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [dob, setDob] = useState("");
  const [gender, setGender] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const signUp = () => {
    setLoading(true);
    fetch("https://quran-server.herokuapp.com/student/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        status: "disabled",
        firstName: firstName,
        lastName: lastName,
        dob: dob,
        mobile: mobile,
        gender: gender,
        email: email,
        password: password,
        confirmPassword: confirmPassword,
        acceptTerms: true,
      }),
    })
      .then((res) => res.json())
      .then((res) => {
        setMessage(res.message);
        if (res.message === "Email is already in use") {
          setLoading(false);
          setVariant("warning");
          setOpen(true);
        } else if (res.message === "Phone Number is already in use") {
          setLoading(false);
          setVariant("warning");
          setOpen(true);
        } else if (
          res.message ===
          "Registration successful, please check your email for verification instructions"
        ) {
          setLoading(false);
          setVariant("success");
          setOpen(true);
        } else {
          setLoading(false);
          setVariant("danger");
          setOpen(true);
        }
      });
  };

  const responseFacebook = (response) => {
    console.log(response);
  };

  const loginWithGoogle = (response) => {
    console.log(response["profileObj"]);
    console.log("in google login function");
    fetch("https://quran-server.herokuapp.com/student/google2", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(response["profileObj"]),
    })
      .then((res) => res.json())
      .then((res) => {
        console.log(res);
        if (!res.account) {
          setMessage("Couldn't Login");
