	constructor(props) {
		super(props);
		// Don't do this!
		this.state = { courses: undefined };
	   }
	   componentDidMount() {

		fetch('https://quran-server.herokuapp.com/course').then((res)=> res.json()).then((res) => {
			console.log("ssssss",res)
			this.setState({courses: res})
	})

	  }
	render(){

		const courses = this.state.courses
		const settings = {
			infinite: true,
			dots: false,
			speed: 500,
			slidesToShow: 3,
			slidesToScroll: 1,
			responsive: [
				{
					breakpoint: 1500,
					settings: {
						slidesToShow: 4,
					}
				},
				{
					breakpoint: 1024,
					settings: {
						slidesToShow: 3,
					}
				},
				{
					breakpoint: 768,
					settings: {
						slidesToShow: 2,
