import React from "react";

import $ from "jquery";

function Cart(props) {
  /* Set values + misc */
  var promoCode;
  var promoPrice;
  var fadeTime = 300;

  /* Assign actions */
  $(".quantity input").change(function () {
    updateQuantity(this);
  });

  $(".remove button").click(function () {
    removeItem(this);
  });

  $(document).ready(function () {
    updateSumItems();
  });

  $(".promo-code-cta").click(function () {
    promoCode = $("#promo-code").val();

    if (promoCode == "10off" || promoCode == "10OFF") {
      //If promoPrice has no value, set it as 10 for the 10OFF promocode
      if (!promoPrice) {
        promoPrice = 10;
      } else if (promoCode) {
        promoPrice = promoPrice * 1;
      }
    } else if (promoCode != "") {
      alert("Invalid Promo Code");
      promoPrice = 0;
    }
    //If there is a promoPrice that has been set (it means there is a valid promoCode input) show promo
    if (promoPrice) {
      $(".summary-promo").removeClass("hide");
      $(".promo-value").text(promoPrice.toFixed(2));
      recalculateCart(true);
    }
  });

  /* Recalculate cart */
  function recalculateCart(onlyTotal) {
    var subtotal = 0;

    /* Sum up row totals */
    $(".basket-product").each(function () {
      subtotal += parseFloat($(this).children(".subtotal").text());
    });

    /* Calculate totals */
    var total = subtotal;

    //If there is a valid promoCode, and subtotal < 10 subtract from total
    var promoPrice = parseFloat($(".promo-value").text());
    if (promoPrice) {
      if (subtotal >= 10) {
        total -= promoPrice;
      } else {
        alert("Order must be more than £10 for Promo code to apply.");
        $(".summary-promo").addClass("hide");
      }
    }

    /*If switch for update only total, update only total display*/
    if (onlyTotal) {
      /* Update total display */
      $(".total-value").fadeOut(fadeTime, function () {
        $("#basket-total").html(total.toFixed(2));
        $(".total-value").fadeIn(fadeTime);
      });
    } else {
      /* Update summary display. */
      $(".final-value").fadeOut(fadeTime, function () {
        $("#basket-subtotal").html(subtotal.toFixed(2));
        $("#basket-total").html(total.toFixed(2));
        if (total == 0) {
          $(".checkout-cta").fadeOut(fadeTime);
        } else {
          $(".checkout-cta").fadeIn(fadeTime);
        }
        $(".final-value").fadeIn(fadeTime);
      });
    }
  }

  /* Update quantity */
  function updateQuantity(quantityInput) {
    /* Calculate line price */
    var productRow = $(quantityInput).parent().parent();
    var price = parseFloat(productRow.children(".price").text());
    var quantity = $(quantityInput).val();
    var linePrice = price * quantity;

    /* Update line price display and recalc cart totals */
    productRow.children(".subtotal").each(function () {
      $(this).fadeOut(fadeTime, function () {
        $(this).text(linePrice.toFixed(2));
        recalculateCart();
        $(this).fadeIn(fadeTime);
      });
    });

    productRow.find(".item-quantity").text(quantity);
    updateSumItems();
  }

  function updateSumItems() {
    var sumItems = 0;
    $(".quantity input").each(function () {
      sumItems += parseInt($(this).val());
    });
    $(".total-items").text(sumItems);
  }

  /* Remove item from cart */
  function removeItem(removeButton) {
    /* Remove row from DOM and recalc cart total */
    var productRow = $(removeButton).parent().parent();
    productRow.slideUp(fadeTime, function () {
      productRow.remove();
      recalculateCart();
      updateSumItems();
    });
  }
  return (
    <main>
      <div className="basket">
        <div className="basket-module">
          <label htmlFor="promo-code">Enter a promotional code</label>
          <input
            id="promo-code"
            type="text"
            name="promo-code"
            maxLength={5}
            className="promo-code-field"
          />
          <button className="promo-code-cta">Apply</button>
        </div>
        <div className="basket-labels">
          <ul>
            <li className="item item-heading">Item</li>
            <li className="price">Price</li>
            <li className="quantity">Quantity</li>
            <li className="subtotal">Subtotal</li>
          </ul>
        </div>
        <div className="basket-product">
          <div className="item">
            <div className="product-image">
              <img
                src="http://placehold.it/120x166"
                alt="Placholder Image 2"
                className="product-frame"
              />
            </div>
            <div className="product-details">
              <h1>
                <strong>
                  <span className="item-quantity">4</span> x Eliza J
                </strong>{" "}
                Lace Sleeve Cuff Dress
              </h1>
              <p>
                <strong>Navy, Size 18</strong>
              </p>
              <p>Product Code - 232321939</p>
            </div>
          </div>
          <div className="price">26.00</div>
          <div className="quantity">
            <input
              type="number"
              defaultValue={4}
              min={1}
              className="quantity-field"
            />
          </div>
          <div className="subtotal">104.00</div>
          <div className="remove">
            <button>Remove</button>
