import React, { useEffect, useState } from "react";

import { useSelector, useDispatch } from "react-redux";
import * as getCourses from "../../store/Action/CoursesAction";

import $ from "jquery";

import Header from "../layout/header/header1";
import Footer from "../layout/footer/footer1";

function Cart(props) {
  const [loading, setLoading] = useState(false);

  const data = useSelector((state) => state.Course.getCourses);

  const dispatcher = useDispatch();

  useEffect(() => {
    getCoursesMethod();
  }, []);

  const getCoursesMethod = async () => {
    setLoading(true);
    await dispatcher(getCourses.getCoursesData());
    setLoading(false);
  };

  console.log("data====> ", data);

  const [array, setrray] = useState([1, 2, 3, 4, 5, 6, 7, 8]);
  /* Set values + misc */
  var promoCode;
  var promoPrice;
  var fadeTime = 300;

  /* Assign actions */
  // $(".quantity input").change(function () {
  //   updateQuantity(this);
  // });

