import {addToCart } from '../ActionType'

const initialState = {
    addToCart: [],
};

export default (state = initialState, action) => {

  switch (action.type) {
    case addToCart:
      return {
        ...state,
        addToCart: action.addToCart,
      };
  }
  return state;
};
